import Drive from './classes/drive';
import {Darwin} from './platforms/darwin';
import {Linux} from './platforms/linux';
import {Windows} from './platforms/windows';
import {Utils} from './utils/utils';

/**
 * Get disk info according current platform.
 *
 * @author Cristiam Mercado
 * @return {Promise<Drive[]>} Promise resolves array of disks and their info.
 */
export function getDiskInfo(): Promise<Drive[]> {

    return new Promise((resolve, reject) => {

        try {

            const platform = Utils.detectPlatform();
            let drivesInfo: Drive[];

            switch (platform) {
                case 'win32':
                    drivesInfo = Windows.run();
                    resolve(drivesInfo);
                    break;
                case 'linux':
                    drivesInfo = Linux.run();
                    resolve(drivesInfo);
                    break;
                case 'darwin':
                    drivesInfo = Darwin.run();
                    resolve(drivesInfo);
                    break;
                default:
                    reject(new Error(`Platform not recognized: ${platform}`));
            }

        } catch (e) {
            reject(e);
        }

    })

}

/**
 * Get disk info according current platform in an syncronous way.
 *
 * @author Cristiam Mercado
 * @return {Drive[]} Array of disks and their info.
 * @throws {Error} Current platform must be win32, linux or darwin.
 */
export function getDiskInfoSync(): Drive[] {

    const platform = Utils.detectPlatform();
    let drivesInfo: Drive[];

    switch (platform) {
        case 'win32':
            drivesInfo = Windows.run();
            return drivesInfo;
        case 'linux':
            drivesInfo = Linux.run();
            return drivesInfo;
        case 'darwin':
            drivesInfo = Darwin.run();
            return drivesInfo;
        default:
            throw new Error(`Platform not recognized: ${platform}`);
    }

}

import {Constants} from '../utils/constants';

import Drive from '../classes/drive';
import {Utils} from "../utils/utils";

/**
 * Class with OSX specific logic to get disk info.
 */
export class Darwin {

    /**
     * Execute specific OSX command to get disk info.
     *
     * @return {Drive[]} List of drives and their info.
     */
    public static run(): Drive[] {

        const drives: Drive[] = [];
        const buffer = Utils.execute(Constants.DARWIN_COMMAND);
        const lines = buffer.split('\n');

        lines.forEach((value, index, array) => {

            if (value !== '') {

                const line: string = value.replace(/ +(?= )/g, '');
                const tokens = line.split(' ');

                const d = new Drive(
                    tokens[0],
                    isNaN(parseFloat(tokens[1])) ? 0 : +tokens[1],
                    isNaN(parseFloat(tokens[2])) ? 0 : +tokens[2],
                    isNaN(parseFloat(tokens[3])) ? 0 : +tokens[3],
                    tokens[4],
                    tokens[5]);

                drives.push(d);

            }

        });

        return drives;
    }

}

/**
 * Class with constants used in the application.
 */
export class Constants {

    /**
     * Command to execute on Windows.
     */
    public static readonly WINDOWS_COMMAND: string = 'wmic logicaldisk get Caption,FreeSpace,Size,VolumeSerialNumber,Description  /format:list';

    /**
     * Command to execute on Linux.
     */
    public static readonly LINUX_COMMAND: string = 'df -P | awk \'NR > 1\'';

    /**
     * Command to execute on OSX.
     */
    public static readonly DARWIN_COMMAND: string = 'df -P | awk \'NR > 1\'';

}

/**
 * Class with drive information.
 *
 * @author Cristiam Mercado
 */
export default class Drive {

    /**
     * Drive filesystem.
     */
    private readonly _filesystem: string;

    /**
     * Blocks associated to disk.
     */
    private readonly _blocks: number;

    /**
     * Used disk space.
     */
    private readonly _used: number;

    /**
     * Available disk space.
     */
    private readonly _available: number;

    /**
     * Disk capacity.
     */
    private readonly _capacity: string;

    /**
     * Indicates the mount point of the disk.
     */
    private readonly _mounted: string;

    /**
     * Constructor for Drive class.
     *
     * @param {string} filesystem Drive filesystem.
     * @param {number} blocks Blocks associated to disk.
     * @param {number} used Used disk space.
     * @param {number} available Available disk space.
     * @param {string} capacity Disk capacity.
     * @param {string} mounted Indicates the mount point of the disk.
     */
    public constructor(filesystem: string, blocks: number, used: number, available: number, capacity: string, mounted: string) {
        this._filesystem = filesystem;
        this._blocks = blocks;
        this._used = used;
        this._available = available;
        this._capacity = capacity;
        this._mounted = mounted;
    }

    /**
     * Drive filesystem.
     *
     * @return Gets drive filesystem.
     */
    get filesystem(): string {
        return this._filesystem;
    }

    /**
     * Blocks associated to disk.
     *
     * @return Gets blocks associated to disk.
     */
    get blocks(): number {
        return this._blocks;
    }

    /**
     * Used disk space.
     *
     * @return Gets used disk space.
     */
    get used(): number {
        return this._used;
    }

    /**
     * Available disk space.
     *
     * @return Gets available disk space.
     */
    get available(): number {
        return this._available;
    }

    /**
     * Disk capacity.
     *
     * @return Gets disk capacity.
     */
    get capacity(): string {
        return this._capacity;
    }

    /**
     * Indicates the mount point of the disk.
     *
     * @return Gets the mount point of the disk.
     */
    get mounted(): string {
        return this._mounted;
    }
}

import * as os from 'os';
import {execSync} from 'child_process';

/**
 * Class with utilitary methods.
 */
export class Utils {

    /**
     * Detects current platform.
     *
     * @return {string} Platform: win32, linux, darwin.
     */
    public static detectPlatform(): string {
        return os.platform().toLowerCase();
    }

    /**
     * Executes a command in SO console.
     *
     * @param {string} command: Command to execute.
     */
    public static execute(command: string): string {
        return execSync(command).toString();
    }
}

